import axios from 'axios';
import {
	TRAER_COMENTARIOS, ERROR_COMENTARIOS, CARGANDO_COMENTARIOS, CAMBIO_TITULO, AGREGADO
} from '../types/comentariosTypes';

export const traerComentarios = () => async (dispatch) => {
	dispatch({ type: CARGANDO_COMENTARIOS });

	try {
		const response = await axios.get('https://jsonplaceholder.typicode.com/comments');

		dispatch({
			type: TRAER_COMENTARIOS,
			payload: response.data
		});
	}
	catch(error) {
		dispatch({
		type: ERROR_COMENTARIOS,
		payload: error.message
		});
	}
};

export const cambioInput = (caso, texto) => (dispatch) => {
	dispatch({
		type: caso,
		payload: texto
	});
};

export const guardar = (cuerpo) => async (dispatch) => {
	dispatch({ type: CARGANDO_COMENTARIOS });

	try {
		const response = await axios.post('httpss://jsonplaceholder.typicode.com/comments', cuerpo);
		window.Materialize.toast('Comentario agregado exitosamente!', 1000);
		dispatch({
			type: AGREGADO
		});
	}
	catch(error) {
		window.Materialize.toast('Algo fallo, intente despues!', 1000);
	}
		dispatch({
		type: ERROR_COMENTARIOS
		});
};
