import React, { Component } from 'react';
import { Card, Col, CardTitle, ProgressBar, Row } from 'react-materialize';
import { connect } from 'react-redux';
import * as fotosActions from '../../actions/fotosActions'
import Cargando from '../General/Cargando.js';
import Fatal from '../General/Fatal.js';

class index extends Component {
	componentDidMount() {
		this.props.traerFotos();
	}


	desplegar = () => (
		this.props.fotos.map((foto2, key) => (
		 	<Col m={7} s={12}>
  				<Card 
  					  header={<CardTitle image={foto2.url}></CardTitle>} 
  					  actions={[<a href='#'>Link</a>]}>
      				  <p style={{fontSize: 10 + 'px'}}>{foto2.title}</p>
    			</Card>
			</Col>
		))
	);

	ponerContenido = () => {
		if (this.props.cargando)
			return <Cargando />

		if (this.props.error)
			return <Fatal mensaje={ this.props.error }/>

		return this.desplegar();
	};

	render () {
	return (
		<div className='row'>
    		{ this.ponerContenido() };
		</div>
	);
	}
}

export default index
