import React from 'react';
import { Input } from 'react-materialize';
import { Icon } from 'react-materialize';
import { Button, row } from 'react-materialize';
import { connect } from 'react-redux';
import * as comentariosActions from '../../actions/comentariosActions'
import { CAMBIO_TITULO, CAMBIO_CONTENIDO } from '../../types/comentariosTypes';
import Cargando from '../General/Cargando';


const Agregar = (props) => {

	const localCambioInput = (event, caso) => {
		props.cambioInput(caso, event.target.value);  {/* la func no es la misma de arriba, es la de Actions*/}
	}

	const agregar = (event) => {
		const { titulo, contenido } = props;   {/*destructura de props */}
		const cuerpo = {
			title: titulo,
			body: contenido
		}
		props.guardar(cuerpo);
	};

	return (
		<div >
			<br />
			<div className='row'>
				<Input  s={12} m={6} label="Titulo" value={ props.titulo } onChange={ (event) => localCambioInput(event, CAMBIO_TITULO) }>  {/*si quito m=6 son 2 lineas */}
					<Icon>input</Icon>
				}
				</Input>
				<Input  s={12} m={6} label="Contenido" value={ props.contenido } type='textarea' onChange={ (event) => localCambioInput(event, CAMBIO_CONTENIDO) }>
					<Icon>account-circle</Icon>
				</Input>
			</div>

			<div className='center-align'>
				<Button waves='light' onClick={ agregar }>button<Icon left>cloud</Icon></Button>
			</div>
			<br/><br/> 
			<Cargando />

			<div className='flex justify_center'>
				<Button waves='light'>button<Icon left>cloud</Icon></Button>
			</div>
			<br/><br/> 

			<row>
				<Button className='col 14 offset-14' waves='light'>button<Icon left>cloud</Icon></Button>
			</row>
		</div>
	)
};

const mapStateToProps = ({ comentariosReducer }) => comentariosReducer;


export default connect(mapStateToProps, comentariosActions)(Agregar);
