import React, { Component } from 'react';
import { Input, Icon, Button, Row } from 'react-materialize';
import { connect } from 'react-redux';
import * as comentariosActions from '../../actions/comentariosActions'
import { CAMBIO_TITULO, CAMBIO_CONTENIDO } from '../../types/comentariosTypes';
import Cargando from '../General/Cargando';



class Guardar extends Component  {

	localCambioInput = (event, caso) => {
		this.props.cambioInput(caso, event.target.value);  {/* la func no es la misma de arriba, es la de Actions*/}
	}

	localGuardar = (event) => {

		if (this.props.titulo == '' || this.props.contenido == '')
		{
			alert("ingresa datos");
			return;
		}

		const { titulo, contenido } = this.props;   {/*destructura de props */}
		const cuerpo = {
			title: titulo,
			body: contenido
		}
	 	this.props.agregar(cuerpo);

	};

render() {

	return (
		<div >
			<br />
			<div className='row'>
				<Input  
						s={12} 
						m={6} 
						label="Titulo" 
						value={ this.props.titulo } 
						onChange={ 
							(event) => this.localCambioInput(event, CAMBIO_TITULO) 
						}
				>  {/*si quito m=6 son 2 lineas */}
					<Icon>input</Icon>
				</Input>
				<Input  
						s={12} 
						m={6} 
						label="Contenido" 
						value={ this.props.contenido } 
						type='textarea' 
						onChange={ 
							(event) => this.localCambioInput(event, CAMBIO_CONTENIDO) 
						}
				>
					<Icon>account-circle</Icon>
				</Input>
			</div>

			{ this.props.mensaje }

			<div className='center-align'>
				<Button 
						className={
							(this.props.cargando) ? 'disabled' : ''
						} 
						waves='light' 
						onClick={ this.localGuardar }
				>
						Guardar
						<Icon left>save</Icon>
				</Button>
			</div>

			{ 
				(this.props.cargando) ? <Cargando /> : '' 
			}

		</div>
	);
}
}


const mapStateToProps = ({ comentariosReducer }) => comentariosReducer;


export default connect(mapStateToProps, comentariosActions)(Guardar);
